import React, { Component } from "react";
import { Link } from "react-router-dom";
class Register extends Component {
  constructor() {
    super();
    this.state = {
      tutorial: [],
      title: "",
      description: "",
      RoleID: "",
      open: false,
      loading: true,
      privilages: [],
      redirect: false,
      isUpdate: false
    };

  
  }
  
  handleInputChange = event => {
    event.preventDefault();
    this.setState({ [event.target.name]: event.target.value });
  };
  
 
  componentWillUnmount() {}
  componentDidMount() {}
  handleSubmit = event => {
    event.preventDefault();
    const data = {
      title: this.state.title,
     description: this.state.description
    };
      this.postData("/api/tutorial", data);
    
  };
  postData(url = ``, data = {}) {
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": localStorage.getItem("token")
      },
      body: JSON.stringify(data)
    })
      .then(response =>
        response.json().then(data => {
          this.fetchRoles();

          if (data.success) {
            alert("Saved successfully");
            this.setRedirect();
          } else {
            alert(data.message);
          }
        })
      )
      .catch(err => {
        alert("Failed");
      });
  }
   setRedirect = () => {
    this.setState({
      redirect: true
    });
  };
  render() {
    if (this.state.redirect) {
      return (window.location = "/Tutorial");
    }
    if (this.state.VerifyEmailWindow) {
      return (window.location = "#/EmailVerification");
    }

    let pstyle = {
      color: "red"
    };
    let btnstyle = {
      background: "#1ab394",
      color: "white"
    };
    let back={
      position: "center",
      top: 0,
      bottom:0,
      left: 0,
      weight: "100%",
      height: "760px",
      // background: "url(../../../app-assets/images/pages/dylan-nolte-CfN8BZn9jF8-unsplash.jpg) no-repeat  center fixed",
      background:"#E2F3F9"

    }
  return (
  
  <section id="services">
      <br/>
      <br/>
    <div className="container" data-aos="fade-up">
      <div className="row vertical-center-row">
          <div className="col-sm-9 col-md-7 col-lg-12 mx-auto">
	 <div id="content" className="content">

      <h1 class="page-header mb-3">Tutorials</h1>
      <div style={{ backgroundColor: "white", borderRadius: "15px" }}>
        <br />
        <div className="container-fluid">
          <div className="row">
            <div className="col-sm-6">
              <div className="ibox-content">
                <form className="form-horizontal" onSubmit={this.handleSubmit}>
                  <div className=" row">
                    <div className="col-sm">
                      <div className="form-group">
                        <label htmlFor="Datereceived" className="fontWeight-bold">
                          Title
                      </label>
                        <input
                          type="text"
                          title="Enter Title"
                          className="form-control"
                          name="title"
                          required
                          onChange={this.handleInputChange}
                        value={this.state.title}
                        />
                      </div>
                    </div>

                  </div>

                  <div className=" row">
                    <div className="col-sm">
                      <div className="form-group">
                        <label htmlFor="Datereceived" className="fontWeight-bold">
                          Description
                      </label>
                        <input
                          type="text"
                          title="description"
                          className="form-control"
                          name="description"
                          required
                         onChange={this.handleInputChange}
                        
                        value={this.state.description}
                        />
                      </div>
                    </div>

                  </div>

                  <div className="row">
                    <div className="col-sm">
                      <div className="row">
                        <div className="col-sm-8"></div>
                        <div className="col-sm-4">
                          <div class="d-flex flex-row float-right">
                             <button className="btn btn-primary p-2" type="submit">
                                Submit
                          </button>
                          </div>
                          <br />
                        </div>
                      </div>
                    </div>
                  </div>
                </form>
              
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
	   </div>
          </div>
        </div>
   
    
  </section>
  );
}
}
export default Register;
